import React from 'react'
import ReactDOM from 'react-dom'

function PartObject(name, excercises) {
    this.name = name
    this.excercises = excercises
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  const parts = [
        new PartObject(part1, exercises1),
        new PartObject(part2, exercises2),
        new PartObject(part3, exercises3)
  ]

  return (
    <div>
      <Header course={course}/>
      <Content props={parts}/>
      <Total excercises={parts} />
    </div>
  )
}

const Header = (course) => {
    return (
        <>
            <h1>{course}</h1>
        </>
    )
}

const Content = (props) => {
    return (
        <>
            <Part part={props.part[0].name} exercises={props.part[0].excercises}/>
            <Part part={props.part[1].name} exercises={props.part[1].excercises}/>
            <Part part={props.part[2].name} exercises={props.part[2].excercises}/>
        </>
    )
}

const Total = (props) => {
    let total = props.forEach(element => {
            total += element.excercises
        })
    return (
        <>
            <p>Number of exercises {total}</p>
        </>)
}

const Part = (props) => {
    return (
        <>
            <p>
                {props.part} {props.exercises}
            </p>
        </>
    )
}

ReactDOM.render(<App />, document.getElementById('root'))
